sed','closed','','2124-revision-v1','','','2016-04-24 18:31:42','2016-04-24 09:31:42','',2124,'http://tsumugi.local/2016/04/2124-revision-v1/',0,'revision','',0),(2126,1,'2016-04-26 08:51:17','0000-00-00 00:00:00','','自動下書き','','auto-draft','open','open','','','','','2016-04-26 08:51:17','0000-00-00 00:00:00','',0,'http://tsumugi.local/?p=2126',0,'post','',0);
